mapper.insert:

insert into myiot_account (user_id, login_name, `password`,
  login_status, create_time, update_time
  )
values (#{userId,jdbcType=VARCHAR}, #{loginName,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR},
  #{loginStatus,jdbcType=BIT}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:

select user_id, login_name, `password`, login_status, create_time, update_time
from myiot_account
where user_id = #{userId,jdbcType=VARCHAR}

SELECT COUNT(1)
FROM myiot_account
WHERE login_name = #{accountName}

SELECT user_id, login_name, password
FROM myiot_account
WHERE login_name = #{accountName}

mapper.update:
UPDATE myiot_account
SET login_status = #{loginStatus}
WHERE user_id = #{userId}

UPDATE myiot_account
SET password = #{password}
WHERE user_id = #{userId}

mapper.delete:
DELETE FROM myiot_account
WHERE user_id IN

  #{userId}
