mapper.select:

select id, `name`, code, parent_id, leaf, leader_id, `status`, role_id
from myiot_department
where id = #{id,jdbcType=VARCHAR}

SELECT count(1)
FROM myiot_department
WHERE parent_id = #{parentId}
AND name = #{name}

SELECT id, `name`, code, parent_id, leaf, leader_id, `status`, role_id
FROM myiot_department
WHERE parent_id = #{parentId}
AND name = #{name}

SELECT COUNT(id)
FROM myiot_department
WHERE parent_id = #{parentId}

SELECT d.id AS id, d.name AS name, d.code AS code, d.leaf AS leaf, d.parent_id AS parentId, d.role_id AS roleId, p.name AS parentName, u.name AS leaderName, u.mobile AS leaderMobile FROM myiot_department d LEFT JOIN myiot_user u ON d.leader_id = u.id LEFT JOIN myiot_department p ON d.parent_id = p.id WHERE 1=1
AND d.parent_id = #{parentId}
AND d.name LIKE CONCAT('%',#{name}, '%')

mapper.update:
UPDATE myiot_department
SET leaf = #{leaf}
WHERE id = #{deptId}
